$(function(){$("#skills-form").submit(function(e){var t=0;$(".skills-input").each(function(){var e=$(this).children("div").children("label").children("input:checked"),n=$(this).children("div").children("label").children("select");if(e.size()>0){var r=e.val(),i=n.val();t+=r*i}});if(t<=810)var n="junior";else if(t>810&&t<=1620)var n="pleno";else var n="senior";$("#total").html(t+" - "+n);e.preventDefault()})});